// Intercetta la richiesta di aggiunta al carrello
cy.intercept('POST', '**/cart/*', {
  statusCode: 200,
  body: {
    success: true
  }
}).as('mockAdd');
// Clicca "Add to cart" sul primo prodotto
cy.get('[data-test=add-to-cart-sauce-labs-backpack]').click();
// Aspetta la chiamata mockata
cy.wait('@mockAdd').its('response.statusCode').should('eq', 200);
// Verifica badge carrello aggiornato
cy.get('.shopping_cart_badge').should('contain.text', '1');

cy.intercept('GET', '**/inventory.json', req => {
  req.reply({
    statusCode: 200,
    body: [{
      id: 1,
      name: 'Mocked Product',
      price: 9.99
    }]
  });
}).as('mockInventory');
// Ricarica pagina per far partire la GET
cy.visit('/inventory.html');
// Verifica che il prodotto mockato sia presente
cy.get('.inventory_item_name').should('contain.text', 'Mocked Product');
cy.get('.inventory_item_price').should('contain.text', '9.99');
