cy.intercept('GET', '**/inventory.json', {
  statusCode: 200,
  body: [{
    id: 1,
    name: 'Mocked Product',
    price: 9.99
  }]
}).as('mockInventory');
cy.visit('/inventory.html'); // punta a baseUrl + inventory.html
cy.wait('@mockInventory');
cy.get('.inventory_item_name').should('contain.text', 'Mocked Product');
cy.get('.inventory_item_price').should('contain.text', '9.99');

cy.intercept('GET', '**/inventory.json', req => {
  req.continue(res => {
    res.body.forEach(item => item.price = 0.01); // prezzo modificato
  });
}).as('mockInventoryPrice');
cy.visit('/inventory.html');
cy.wait('@mockInventoryPrice');
cy.get('.inventory_item_price').first().should('contain.text', '0.01');
