cy.intercept('GET', '/inventory.json', {
  statusCode: 200,
  body: [{
    id: 999,
    name: 'Mocked Sauce Labs Backpack',
    description: 'Test product',
    price: 123.45,
    image: 'https://www.saucedemo.com/images/sauce-backpack.png'
  }]
}).as('mockInventory');
cy.reload(); // Ricarica la pagina per attivare la chiamata GET
cy.wait('@mockInventory').its('response.statusCode').should('eq', 200);
cy.get('.inventory_item_name').first().should('contain.text', 'Mocked Sauce Labs Backpack');

cy.intercept('GET', '/inventory.json', req => {
  req.continue(res => {
    res.body.forEach(item => item.price = 9.99);
  });
}).as('modifiedInventory');
cy.reload();
cy.wait('@modifiedInventory');
cy.get('.inventory_item_price').first().should('contain.text', '9.99');
